function skillsFunction(){let s=document.querySelectorAll(".progress");for(let e=0;e<s.length;e++){var r=s[e].attr("data-value"),o=s[e].getElementsByClassName(".progress-left")[0].getElementsByClassName(".progress-bar")[0],t=s[e].getElementsByClassName(".progress-right").getElementsByClassName(".progress-bar")[0];r>0&&(r<=50?t.css("transform","rotate("+a(r)+"deg)"):(t.css("transform","rotate(180deg)"),o.css("transform","rotate("+a(r-50)+"deg)")))}function a(e){return e/100*360}}